document.addEventListener("DOMContentLoaded",function(){var e=document.getElementById("contrastToggle"),t=document.getElementById("navToggle"),n=document.getElementById("navList");if(e){if(localStorage.getItem("highContrast")==="true"){document.body.classList.add("high-contrast");e.setAttribute("aria-pressed","true")}e.addEventListener("click",function(){document.body.classList.toggle("high-contrast");var o=document.body.classList.contains("high-contrast");localStorage.setItem("highContrast",o),e.setAttribute("aria-pressed",String(o))})}if(t&&n){t.addEventListener("click",function(){var o=t.getAttribute("aria-expanded")==="true";t.setAttribute("aria-expanded",String(!o));n.style.display=o?"none":"flex"})} });
